import React, { useEffect, useState } from "react";
import SyntaxHighlighter from 'react-syntax-highlighter';
import { docco } from 'react-syntax-highlighter/dist/esm/styles/hljs';
import { useParams } from "react-router";
import { useLocation } from "react-router";

const CodeBlockPage1 = () => {

    const location = useLocation();
    console.log(location.state);

    const code = location.state.codeBlocks[0].code;
    const id = location.state.codeBlocks[0]._id;
    const solution = location.state.codeBlocks[0].solution;
    const explanation = location.state.codeBlocks[0].explanation;

    console.log(code);
    console.log(id);
    console.log(solution);

    const [value, setValue] = useState(code); 



    const handleSubmit = () => {
        if(solution === value){
            alert(":)")
        }
        else{
            alert("Your answer is not correct :(")
        }
    };

    const handleChange = event => {
        setValue(event.target.value)
    };


    return(
        <div>
            <h1>Change Your Code Here</h1>
            <SyntaxHighlighter language="javascript" style={docco}>{code}</SyntaxHighlighter>
            <p>Explanation: {explanation} </p>
             <textarea value={value} onChange={handleChange} rows={10} cols={60}></textarea>
            <button onClick={handleSubmit}>Submit Soluton</button>
            <p>The solution is in the console</p>
        </div>

    )
};

export default CodeBlockPage1;
