import React, { useEffect, useState } from "react";
import SyntaxHighlighter from 'react-syntax-highlighter';
import { docco } from 'react-syntax-highlighter/dist/esm/styles/hljs';
import { useParams } from "react-router";
import { useLocation } from "react-router";
import hljs from 'highlight.js';

const TestPage = () => {


    const {codeBlockid} = useParams();
    const [codeBlock, setCodeBlock] = useState('');
    console.log(codeBlockid);


    useEffect( () => {
        const fetchCodeBlock = async () => {
            const response = await fetch(`http://localhost:5000/api/codeBlocks/${codeBlockid}`)
            const json = await response.json()

            if(response.ok) {
                setCodeBlock(json)
                console.log(response)
            }
        }

        fetchCodeBlock()
    }, []);

    console.log(codeBlock);

    const [value, setValue] = useState(codeBlock.code); 

    useEffect(() => {
        if (codeBlock) {
          hljs.highlightAll();
        }
      }, [codeBlock]);



    const handleSubmit = () => {
        if(codeBlock.solution === value){
            alert(":)")
        }
        else{
            alert("Your answer is not correct :(")
        }
    };

    const handleChange = event => {
        setValue(event.target.value)
    };


    return(
        <div>
            <h1>Change Your Code Here</h1>
            <SyntaxHighlighter language="javascript" style={docco}>{codeBlock.code}</SyntaxHighlighter>
            <p>Explanation: {codeBlock.explanation} </p>
             <textarea value={codeBlock.code} onChange={handleChange} rows={10} cols={60}></textarea>
            <button onClick={handleSubmit}>Submit Soluton</button>
            <p>The solution is in the console</p>

        </div>

    )
};

export default TestPage;
